</template>

<script>
  export default {
    props: ['edges', 'vertices', 'fragmentation', 'end'],
    watch: {
      fragmentation: {
        handler (fragmentation) {
          setTimeout(() => {
            this.fragment();
          }, 100);
        },
        deep: true,
        immediate: true
      },
      edges:{
        handler (edges) {
          if (this.end) {
            this.fragment();
          }
        },
        immediate: true,
        deep: true
      }
    },
    methods: {
      fragment () {
        this.edges.forEach(edge => {
          let line = document.getElementsByClassName(edge.vertices.join(' '))[0];
          line.style.display = edge.isVisible ? 'inline-block' : 'none';
        });
      }
    }
  }
</script>